mapper.update:
update t_address set
recv_name=#{recvName}, recv_province=#{recvProvince}, recv_city=#{recvCity}, recv_area=#{recvArea}, recv_address=#{recvAddress}, recv_district=#{recvDistrict}, recv_phone=#{recvPhone}, recv_tel=#{recvTel}, recv_zip=#{recvZip}, tag=#{tag},
is_default=#{isDefault},
modified_user = #{modifiedUser}, modified_Time = #{modifiedTime} where uid=#{uid}
and id=#{id}

mapper.select:
select id, uid, recv_name AS recvName, recv_province AS recvProvince, recv_city AS recvCity, recv_area AS recvArea, recv_address AS recvAddress, recv_district AS recvDistrict, recv_phone AS recvPhone, recv_tel AS recvTel, recv_zip AS recvZip, tag, is_default AS isDefault, created_user AS createdUser, created_time AS createdTime, modified_user AS modifiedUser, modified_time AS modifiedTime from t_address
where ${where}
order by ${orderBy}
limit #{offset},#{countPerPage}
